GO

ALTER TABLE dbo.[PaymentPlanAccountFee]
	ADD CONSTRAINT FK_PaymentPlanAccountFee_PaymentPlanAccountID FOREIGN KEY (PaymentPlanAccountID) REFERENCES PaymentPlanAccount(PaymentPlanAccountID);
GO



